function progressHandles = fProgressDialog(varargin)





message = 'Progress';
value = 0;
est_text = 'Estimated time remaining: ';  

if nargin < 1   
    % progressdlg; creates default progress dialog
    progressHandles = [];
    
elseif nargin == 1 
    if ischar(varargin{1})       
        % progressdlg('message'); creates default progress dialog with message
        message = varargin{1};
        progressHandles = [];
        
    elseif isnumeric(varargin{1}) 
        % progressdlg(n); creates default progress dialog with initial value n
        % or updates existing progress dialog to value n
        value = varargin{1};
        fig = findobj('Tag','ProgressDialog');
        if isempty(fig)                                                    
            progressHandles = [];                                          
        else
            progressHandles.progress = findobj('Parent',fig,'-and','Tag','ProgressBar');
            progressHandles.text = findobj('Parent',fig,'-and','style','text');
            progressHandles.figure = fig;
        end
        
    elseif isfield(varargin{1},'figure')   
        % progressdlg(handle_struct); does nothing
        progressHandles = varargin{1};
        return;
        
    end
    varargin(:) = [];
    
elseif isfield(varargin{1},'figure')          
    % progressdlg(handle_struct,'Name',Value,...); updates existing
    % progress dialog (defined in handle_struct) with parameter pairs Name/Value
    progressHandles = varargin{1};
    if ~ishandle(progressHandles.figure)
        progressHandles = [];
        return;    
    else
        if nargin == 2 && isnumeric(varargin{2})        
            % progressdlg(handle_struct,n); updates existing progress dialog 
            %(defined in handle_struct) to value n
            value = varargin{2};
        end
        varargin(:) = [];
    end
else
    % progressdlg('Name',Value,...); creates progress dialog with parameter pairs Name/Value
    progressHandles = [];
end

p = inputParser;
states = {'on','off'};
addParamValue(p,'title','');
addParamValue(p,'size',1);
addParamValue(p,'value',value);
addParamValue(p,'min',0);
addParamValue(p,'max',100);
addParamValue(p,'position',[],@isnumeric);
addParamValue(p,'string',message);
addParamValue(p,'cancel','on',@(x) any(validatestring(x,states)))

%if first input argument is not a handle structure, create new dialog
if isempty(progressHandles)
    parse(p,varargin{1:end});
    prog = p.Results;
    
    if isempty(prog.position)
       screen = get(0,'ScreenSize');
       prog.position = ceil([(screen(3)-400*prog.size)/2 (screen(4)-150*prog.size)/2]);
    end
    
    progfig = figure('Menubar','none','Numbertitle','off','Resize','off',...                                 
                     'Tag','ProgressDialog','Units','pixels','Visible','off',...
                     'Name',prog.title,'Position',[200 200 400 150]);
    %fPlaceFig(progfig,'small');
    if ispc
        set(progfig,'Color',[236 233 216]/255);
    end
    uiColor = get(progfig,'Color');
   
    text(1) = uicontrol(progfig,'style','text','units','normalized','pos',[0.05 0.7 0.9 0.15],...                       
                      'hor','left','backgroundcolor',uiColor,'string',prog.string,...
                      'Fontunits','normalized','FontSize',0.6); 
                  
    text(2) = uicontrol(progfig,'style','text','units','normalized','pos',[0.05 0.3 0.9 0.15],...                     
                                'hor','left', 'backgroundcolor',uiColor,'string',est_text,...
                                'Fontunits','normalized','FontSize',0.6);    
                            
    text(3) = uicontrol(progfig,'style','text','units','normalized','pos',[0.8 0.5 0.15 0.15],...                 
                                'hor','right','backgroundcolor',uiColor,'string','0 %',...
                                'Fontunits','normalized','FontSize',0.6);     
                            
    if strcmp(prog.cancel,'on')
        uicontrol(progfig,'style','pushbutton','units','normalized','pos',[0.3 0.1 0.4 0.15],...         
                          'hor','right','string','cancel','Callback','close');        
    end                  
    
    jProgressBar = javaObjectEDT('javax.swing.JProgressBar');
    set(jProgressBar,'Background',uiColor,'Minimum',prog.min,'Maximum',prog.max,'Value',prog.value);
    [hProgressBar,cProgressBar] = javacomponent(jProgressBar,[20 70 300 40],progfig);
    set(cProgressBar,'units','normalized','Tag','ProgressBar');
    set(progfig,'Position',[ceil(prog.position) 400*prog.size 150*prog.size]);
    set(progfig,'Visible','on');
    progressHandles.figure = progfig;
    progressHandles.progress = hProgressBar;
    progressHandles.text = text;
    set(progressHandles.figure,'UserData',clock);
else
    parse(p,varargin{2:end});
    prog = p.Results;  
    set(progressHandles.progress,'Value',prog.value);  
end


%{

% Set defaults for variables not passed in
if nargin < 1,
    fractiondone = 0;
end

try
    % Access progfig to see if it exists ('try' will fail if it doesn't)
    dummy = get(progfig,'UserData');
    
    % If progress bar needs to be reset, close figure and set handle to empty
    if fractiondone == 0
        delete(progfig) % Close progress bar
        progfig = []; % Set to empty so a new progress bar is created
    end
catch
    progfig = []; % Set to empty so a new progress bar is created
end

if nargin < 2 && isempty(progfig),
    message = '';
end
if nargin < 3 && isempty(progfig),
    progtitle = '';
end
if nargin < 5
    abort = 1;
end
if nargin < 6
    draw = 1;
end
    
% If task completed, close figure and clear vars, then exit
percentdone = floor(100*fractiondone);
if percentdone == 100 % Task completed
    delete(progfig) % Close progress bar
    clear progfig progpatch starttime lastupdate % Clear persistent vars
    return
end

est_text = 'Estimated time remaining: ';  

% Create new progress bar if needed
if isempty(progfig)

    pgx = [0 0];
    pgy = [41 43];
    pgw = [57 57];
    pgh = [0 -3];
    %%%%%%%% END SET WINDOW SIZE AND POSITION %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
     
     % Initialize progress bar
    progfig = figure('menubar','none',...                   % Turn figure menu display off
         'numbertitle','off',...                            % Turn figure numbering off
         'resize','off',...                                 % Turn of figure resizing
         'tag','timebar',...
         'Units','normalized',...
         'Visible','off');                             
     
    fPlaceFig(progfig,'small');
    
    if ispc
        set(progfig,'Color',[236 233 216]/255);
    end
    
    c = get(progfig,'Color');
    
    work.progtitle = progtitle;                             % Store initial values for title
    work.message = message;                                 % Store initial value for message
    set(progfig,'userdata',work);                           % Save text in figure's userdata
  
    % ------- Delete Me (Begin) ----------
%     winchangeicon(progfig,'MavLogo.ico');                   % Change icon (Not Released)
    % ------- Delete Me (End) ------------
     
    axis(1) = axes('parent',progfig,...                               % Set the progress bar parent to the figure
        'units','normalized',...                            % Provide axes units in pixels
        'pos',[0.05 0.5 0.75 0.15],...                       % Set the progress bar position and size
        'xlim',[0 1],...                                    % Set the range from 0 to 1
        'visible','off',...                                 % Turn off axes
        'drawmode','fast');                                 % Draw faster (I dunno if this matters)
    
    set(axis(1),'Units','pixels');
    Pos = get(axis(1),'Position');
    
    if Pos(3)<285
        imshow(progimage(c,Pos(3)));                                      % Draw Progress Bar Image   
    else
        Pos(3) = 285;
        set(axis(1),'Position',Pos);
        imshow(progimage(c,285));             
    end
        
    progpatch = patch(...
        'XData',            [4 4 Pos(3)-2 Pos(3)-2],...                   % Initialize X-coordinates for patch
        'YData',            [4 10 10 4],...                   % Initialize Y-coordinates for patch
        'Facecolor',        'w',...                         % Set Color of patch
        'EraseMode',        'normal',...                    % Set Erase mode, so we can see progress image
        'edgecolor',        'none');                        % Set the edge color of the patch to none

    text(1) = uicontrol(progfig,'style','text',...     
        'units','normalized',...                            % Prepare message text (set the style to text)
        'pos',[0.05 0.7 0.9 0.15],...                       % Set the textbox position and size
        'hor','left',...                                    % Center the text in the textbox
        'backgroundcolor',c,...                             % Set the textbox background color                  
        'string',message);                                  % Set the text to the input message
    
    text(2) = uicontrol(progfig,'style','text',... 
        'units','normalized',...                          % Prepare static estimated time text
        'pos',[0.05 0.3 0.9 0.15],...                     % Set the textbox position and size
        'hor','left',...                                  % Left align the text in the textbox
        'backgroundcolor',c,...                           % Set the textbox background color
        'foregroundcolor',0*[1 1 1],...                   % Set the text color
        'string',est_text);                               % Set the static text for estimated time
    
    text(3) = uicontrol(progfig,'style','text',... 
        'units','normalized',...                         % Prepare the percentage progress
        'pos',[0.825 0.48 0.15 0.15],...                 % Set the textbox position and size
        'hor','right',...                                % Left align the text in the textbox
        'backgroundcolor',c,...                          % Set the textbox background color
        'string','');                                    % Initialize the progress text as blank
    
    if abort == 1
        uicontrol(progfig,'style','pushbutton',...   
            'units','normalized',...
            'pos',[0.3 0.1 0.4 0.15],...         
            'hor','right',...                                                                       
            'string','abort',...
            'Callback','close');     
    end
    
    drawnow;
    lastupdate = clock - 1;

    % Task starting time reference
    if isempty(starttime) || (fractiondone == 0)
        starttime = clock;
    end
    
end

% Enforce a minimum time interval between updates
if etime(clock,lastupdate) < 0.01
    return
end

% Update progress patch
XData = get(progpatch,'XData');
width = XData(3)-4; 
XData(1:2) = 4+fractiondone*width;
set(progpatch,'XData',XData)

% Set all dynamic text
runtime = etime(clock,starttime);
if ~fractiondone,
    fractiondone = 0.001;
end
work = get(progfig,'userdata');
try 
    progtitle;
catch
    progtitle = work.progtitle;
end
try 
    message;
catch
    message = work.message;
end
if timeleft==-1
    timeleft = runtime/fractiondone - runtime;
end
timeleftstr = sec2timestr(timeleft);
titlebarstr = sprintf('%2d%%  %s',percentdone,progtitle);
set(progfig,'Name',titlebarstr)
set(text(1),'string',message);
set(text(2),'string',[est_text timeleftstr]);
set(text(3),'string',[num2str(percentdone) ' % ']);

% Force redraw to show changes
if draw == 1
    drawnow expose;
end

% Record time of this update
lastupdate = clock;


% ------------------------------------------------------------------------------
function timestr = sec2timestr(sec)

% Convert seconds to hh:mm:ss
h = floor(sec/3600); % Hours
sec = sec - h*3600;
m = floor(sec/60); % Minutes
sec = sec - m*60;
s = floor(sec); % Seconds

if isnan(sec),
    h = 0;
    m = 0;
    s = 0;
end

if h < 10; h0 = '0'; else h0 = '';end     % Put leading zero on hours if < 10
if m < 10; m0 = '0'; else m0 = '';end     % Put leading zero on minutes if < 10
if s < 10; s0 = '0'; else s0 = '';end     % Put leading zero on seconds if < 10
timestr = strcat(h0,num2str(h),':',m0,...
          num2str(m),':',s0,num2str(s));



